import React from "react";
import Card from "../card/Card";
import { connect } from "react-redux";
import { removeFav } from "../../redux/actions";

function Favorites({ myFavorites, onClose, removeFav }) {
  const cardsContainer = {
    display: "flex",
    flexWrap: "Wrap",
    justifyContent: "space-evenly",
  };

  const closeFav = (id) => {
    onClose(id);
    removeFav(id);
  };

  //Male, Female, Genderless y unknown.

  return (
    <div>
      <select name="order" defaultValue={"DEFAULT"}>
        <option value="Ascendente">Ascendente</option>
        <option value="Ascendente">Ascendente</option>
        <option value="Descendente">Descendente</option>
      </select>
      <h1>Favorites</h1>
      <div style={cardsContainer}>
        {myFavorites.map((characters) => (
          <Card
            key={characters.id}
            id={characters.id}
            name={characters.name}
            status={characters.status}
            species={characters.species}
            gender={characters.gender}
            origin={characters.origin?.name}
            image={characters.image}
            onClose={() => closeFav(characters.id)}
          />
        ))}
      </div>
    </div>
  );
}

function mapState(st) {
  return {
    myFavorites: st.myFavorites,
  };
}

function mapDispatch(d) {
  return {
    removeFav: (id) => d(removeFav(id)),
  };
}

export default connect(mapState, mapDispatch)(Favorites);
